import { Component } from '@angular/core';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent {
searchText : string ='Mens wear';
onEnter(event:any){
  this.searchText=event.target.value;
}
}
<!-- <p>search works!</p> -->
<div class="ekart--search--product">
    <input class="ekart-search-product-input" [value]="searchText" (input)="onEnter($event)">
    <button class="btn btn-search">Search</button>
</div>
<div class="ekart-search-result-for"><p><strong>Search result for:</strong>{{searchText}}</p></div>

