import { HttpClient } from '@angular/common/http';
import { Component, EventEmitter, Output } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  userName: string ="";
  password: string ="";
   isLogin:boolean=false; 
  constructor(private router: Router,private http: HttpClient) {}

  login() {
     this.isLogin=true;
      alert('Login successful. Now you can book a tour !!!')
      this.router.navigateByUrl('/booking');
    }
}
<!-- <p>login works!</p> -->
<div class="login-container d-flex flex-row">
  <div class="image-container  border m-2 ">
    <img class="w-100 h-100" src="/assets/images/login1.jpeg" alt="">
  </div>
  <!-- <div class="container border w-25" > -->
    <div class="card  m-2">
    <form class="form-group m-5">
      <h1 class="welocome-text">WelCome</h1>
      <p class="login-sub-heading">Login with us</p>
      <div>
        <label for="name">Name</label>
        <input type="text"  class="form-control" id="name"   placeholder="Enter Name">
      </div>

      <div>
        <label>Password</label>
        <input type="password" class="form-control" id="password" class="form-control" placeholder="Enter Password">
      </div>

      <div>
        <button type="submit" class="btn btn-primary d-block  mt-2 w-100" (click)="login()">Login</button>
      </div>

       <div>
        <a   routerLink="/forgot-password">Forgot Password ?</a>
       </div>

       <div>
        <p>Don't have account?<a routerLink="/register" >Register Now</a></p>
       </div>

    </form>
    </div>
  <!-- </div> -->
</div>
