import { Component } from '@angular/core';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent {
  yourName:string='';
  Name : string = "Baswa"
  product={
    mobileName :'iPhone 13',
    Price : 5000,
    color: 'Black',
    discount: 10,
    inStock:10,
    image:"assets/SChethri.jpg"
  }


getDiscountedPrice(){
  return '$'+(this.product.Price-(this.product.Price*this.product.discount/100))
}

onEnter(event:any){ 
  // console.log(event)
  this.Name=event.target.value;
}

onDecrease(){
  if(this.product.inStock>0){
    this.product.inStock-= 1;
  }
}
onIncrease(){
  this.product.inStock+= 1;
}
}

<!-- <p>product-list works!</p> -->
<div class="ecart--product--container">
    <img [src]='product.image' style="height: 10%;width: 10%;">
    <h3>Mobile Name : {{product.mobileName}}</h3>
    <h3>Price :{{'$'+product.Price}}</h3>
    <h3>Color : {{product.color}}</h3>
    <h3>Discounted Price : {{getDiscountedPrice()}}</h3>
    <h3>{{product.inStock>0 ? 'only'+product.inStock+'in stock' : 'out of stock'}}</h3>
    <button [disabled]="!(product.inStock>0)">Buy</button>
    <br>
    <button (click)="onDecrease()">-</button>
    <span>{{product.inStock}}</span>
    <button (click)="onIncrease()">+</button>
    <br>
    <input type="text"  (input)="onEnter($event)">
    <h4>{{Name}}</h4>

    <br>
    <input type="text" [(ngModel)]="yourName">
    <h4>{{yourName}}</h4>
</div>
